import { Stats } from 'fs';

type SimpleDirent = {
    name: string;
    dir: boolean;
};
type SimpleStats = Stats & {
    dir: boolean;
};

declare function readdir(path: string, withFileTypes?: false): Promise<string[]>;
declare function readdir(path: string, withFileTypes?: true): Promise<SimpleDirent[]>;
declare function readFile(path: string): Promise<string>;
declare function stat(path: string): Promise<SimpleStats>;
declare function writeFile(path: string, data: string | ArrayBufferView | DataView): Promise<void>;
declare function rm(path: string, options?: {
    recursive?: boolean;
    force?: boolean;
}): Promise<void>;
declare const fs: {
    readdir: typeof readdir;
    readFile: typeof readFile;
    stat: typeof stat;
    writeFile: typeof writeFile;
    rm: typeof rm;
};

export { fs as default };
